<template>
  <div id="app">
    <header>
      <h1>Homes & Beyond</h1>
      <p>{{ filteredProperties.length }} Properties Found</p>
    </header>

    <div class="controls">
      <input 
        v-model="searchQuery" 
        placeholder="Search by title or location..."
      >
      <select v-model="sortOrder">
        <option value="low">Sort: Low → High</option>
        <option value="high">Sort: High → Low</option>
      </select>
    </div>

    <div class="grid">
      <div 
        class="card" 
        v-for="property in sortedProperties" 
        :key="property.id"
      >
       <div 
  class="bookmark"
  :class="{bookmarked: favourites.includes(property.id)}"
  @click="toggleFavourite(property.id)"
  >
 fav
</div>
      <img :src="property.image">
      
    <div v-if="!property.available" class="badge">Not Available</div>
    <section>
        
        <div class="info">
          <h2>{{ property.title }}</h2><br><br><br>
          <strong>{{ property.location }}</strong><br><br><br>
          <strong>R{{ property.price.toLocaleString() }}</strong><br><br><br>
          <strong>Type: {{ property.type }}</strong>
        </div>
        
      </section>
    </div>
  </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      searchQuery: "",
      sortOrder: "low",
      favourites: [],
      properties: [
        { id: 1, title: "Sea View Apartment", location: "Camps Bay", price: 2400, type: "Apartment", available: true, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWwNNAV2UEMAlWNPlVEHiD4hRwd_ZvVZ-N8w&s" },
        { id: 2, title: "farmily Apartment", location: "Woodstock", price: 9400, type: "Apartment", available: true, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5mgkZS7bSVbIgYYS9acU8KU-5fMccZ1x1-A&s" },
        { id: 3, title: "City studio", location: "Athlone", price: 2400, type: "Studio", available: true, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ4U4m-CQuakO6j0-xrMtwfKTEqQUF4nKV07w&s" },
        { id:4, title: "Family Home", location: "Stelenbosch", price: 9600, type: "House", available: true, image: "data:image/jpeg;base64," },
        { id:5, title: "City Studio", location: "Gordens bay", price: 900, type: "Studio", available: true, image: "data:image/jpeg;base64," },
        { id:6, title: " Mountain view house ", location: "Cape Town CBD", price: 1400, type: "house", available: true, image: "data:image/jpeg;base64,"},
        { id:7, title: "large Loft", location: "Strand", price: 700, type: "Loft", available: true, image: "data:image/jpeg;base64," },
        { id:8, title: "Modern Loft", location: "Paarl", price: 3200, type: "Loft", available: false, image: "data:image/jpeg;base64," },
        { id:9, title: "City Studio", location: "Cape Town CBD", price: 1900, type: "Studio", available: true, image: "data:image/jpeg;base64," },
      ],
    };
  },

  computed: {
    filteredProperties() {
      const q = this.searchQuery.toLowerCase();
      return this.properties.filter(p =>
        p.title.toLowerCase().includes(q) ||
        p.location.toLowerCase().includes(q)
      );
    },

    sortedProperties() {
      const list = [...this.filteredProperties];
      return this.sortOrder === "low"
        ? list.sort((a, b) => a.price - b.price)
        : list.sort((a, b) => b.price - a.price);
    }
  },

  methods: {
    toggleFavourite(id) {
      if (this.favourites.includes(id)) {
        this.favourites = this.favourites.filter(f => f !== id);
      } else {
        this.favourites.push(id);
      }
    }
  }
};
</script>

